int result = 0;
int maxDeviation = 3;
int safeLines = 0;

foreach (var line in input)
{

    bool ascending = false;
    int[] lineArr = line.Split(" ").Select(int.Parse).ToArray();
    bool safeLine = true;

    if (lineArr.Length <= 2)
    {
        throw new Exception("lines too short");
    }

    if (lineArr[0] < lineArr[1])
    {
        ascending = true;
    }

    for (int i = 0; i < lineArr.Length - 1; i++)
    {
        int difference = lineArr[i] - lineArr[i+1];
        if (difference == 0||
            (difference > maxDeviation && !ascending) || (difference < -maxDeviation && ascending)||
            (ascending && difference > 0) || (!ascending && difference < 0))
        {
            safeLine = false;
            break;
        }
    }
    if (safeLine)
    {
        safeLines++;
    }
}
